mapper.select:
select a.* from 
(select a.vehicle_id, a.car_num, a.model, a.company_id, a.status, a.vehicle_type, a.img_url, a.type, a.start_time, a.end_time, a.contacts, a.telephone, a.remark,
a.create_by, a.create_time, a.update_by, a.update_time, a.user_id, tc.company_name,
CASE
WHEN tb.blacklist_status IS NULL THEN'1'
ELSE tb.blacklist_status
END AS blacklist_status
from t_vehicle a
left join t_company tc on tc.company_id = a.company_id
LEFT JOIN t_blacklist tb ON tb.blacklist_name = a.car_num AND tb.blacklist_status = '0')  as a                         

     and a.car_num like concat('%',#{carNum},'%')
     and a.model = #{model}
     and a.company_id = #{companyId}
     and a.status = #{status}
     and a.vehicle_type = #{vehicleType}
     and a.img_url = #{imgUrl}
     and a.type = #{type}
     and a.start_time = #{startTime}
     and a.end_time = #{endTime}
     and a.contacts = #{contacts}
     and a.telephone = #{telephone}
     and a.user_id = #{userId}
    and a.blacklist_status = 1

order by a.vehicle_id desc


where car_num = #{carNum}
order By end_time
limit 1

mapper.update:
update t_vehicle
car_num = #{carNum}, model = #{model}, company_id = #{companyId}, status = #{status}, vehicle_type = #{vehicleType}, img_url = #{imgUrl}, type = #{type}, start_time = #{startTime}, end_time = #{endTime}, contacts = #{contacts}, telephone = #{telephone}, remark = #{remark}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where vehicle_id = #{vehicleId}
